 package ch03.sec10;

public class AssignmentOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		
		result += 10;
		result -= 5;
		
		System.out.println(result);
		
		result *= 10;
		System.out.println(result);
		result /= 5;
		System.out.println(result);

 


	}

}
