package ch04.sec04;

public class PrintFrom1To10Example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 1; i <= 10; i++) {
			System.out.print(i + " ");
		}
		
		
		for(int a = 0, b = 100; a<=50 && b>=50; a++, b--)
		{
			System.out.print(a +" ,"+ b );
		}
		

	}

}
